// Simulate API delay
export const simulateAPIDelay = (minMs: number = 2000, maxMs: number = 5000): Promise<void> => {
  const delay = Math.random() * (maxMs - minMs) + minMs;
  return new Promise(resolve => setTimeout(resolve, delay));
};